mapper.select:
select id, batch_number, device_name, device_sn, device_identification, create_status, failure_case
from device_batch_detail
where id = #{id}

select
id, batch_number, device_name, device_sn, device_identification, create_status, failure_case
from device_batch_detail


        and id = #{id}


        and batch_number = #{batchNumber}


        and device_name = #{deviceName}


        and device_sn = #{deviceSn}


        and device_identification = #{deviceIdentification}


        and create_status = #{createStatus}


        and failure_case = #{failureCase}

select count(1)
from device_batch_detail


        and id = #{id}


        and batch_number = #{batchNumber}


        and device_name = #{deviceName}


        and device_sn = #{deviceSn}


        and device_identification = #{deviceIdentification}


        and create_status = #{createStatus}


        and failure_case = #{failureCase}
