mapper.select:
select
id,event_name,event_code,event_type,template_identification,product_identification,status,description,create_by,create_time,update_by,update_time

from product_event


        and id = #{id}


        and event_name = #{eventName}


        and event_code = #{eventCode}


        and event_type = #{eventType}



            and (template_identification = #{templateIdentification} or product_identification = #{productIdentification})


            and template_identification = #{templateIdentification}


            and product_identification = #{productIdentification}



        and status = #{status}


        and description = #{description}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


order by update_time desc

select count(1)
from product_event


        and id = #{id}


        and event_name = #{eventName}


        and event_code = #{eventCode}


        and event_type = #{eventType}



            and (template_identification = #{templateIdentification} or product_identification = #{productIdentification})


            and template_identification = #{templateIdentification}


            and product_identification = #{productIdentification}



        and status = #{status}


        and description = #{description}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
